package com.Practice;

public class Switchstatements {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
// switch case statement 
		//use collon after each case
		int number =10;
		String name = "Hitesh";
		
	switch (name) {
	case "Arun":
	System.out.println("Hi" + name);
	break;
	
	case "Mani" : System.out.println("Hi" + name);
	break;
	
	case "Srinath" : System.out.println("Hi" + name);
	break;
	
	case "Ajai" : System.out.println("Hi" + name);
	break;
	
	default : System.out.println("your name is not in that list");
	break;
	}
	}

}
