package com.class1;

public class Mathfunctions {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println(Math.abs(-23.5));   //absolute function - return only positve number
		System.out.println(Math.floor(8.5));   //floor function - return value round down
		System.out.println(Math.ceil(5.3));    // ceil function - return value round up
		System.out.println(Math.max(2.3, 5.6));  //max function - return only max number
		System.out.println(Math.min(2.3, 9.3));   //min function - return only min number
		System.out.println(Math.pow(3, 2));        // 3^2 power function
		System.out.println(Math.sqrt(25));        //square root
		System.out.println(Math.sin(0));
	}

}
